require 'character_diffs'

describe '#character_diffs' do
    let(:test1_a) {'cde'}
    let(:test1_b) {'abc'}
    
    let(:test2_a) {'fcrxzwscanmligyxyvym'}
    let(:test2_b) {'jxwtrhvujlmrpdoqbisbwhmgpmeoke'}
    
    let(:test3_a) {''}
    let(:test3_b) {''}

    it 'returns total number of characters to delete to make an anagram' do
        expect(character_diffs(test1_a, test1_b)).to eq(4)
        expect(character_diffs(test2_a, test2_b)).to eq(30)
        expect(character_diffs(test3_a, test3_b)).to eq(1000)
    end

end